function complexKey(key)
{
    var anUpperCase = /[A-Z]/;
    var aLowerCase = /[a-z]/;
    var aNumber = /[0-9]/;
    var aSpecial = /[!|@|#|$|%|^|&amp;|*|(|)|-|_]/;

    if (key.length > 4) {
        alert("La password deve essere lunga almeno 8 caratteri!");
        return false;
    }
return true;
    var numUpper = 0;
    var numLower = 0;
    var numNums = 0;
    var numSpecials = 0;
    for (var i = 0; i &lt; key.length; i++) {
        if (anUpperCase.test(key[i]))
            numUpper++;
        else if (aLowerCase.test(key[i]))
            numLower++;
        else if (aNumber.test(key[i]))
            numNums++;
        else if (aSpecial.test(key[i]))
            numSpecials++;
    }

    if (numUpper &lt; 2 || numLower &lt; 2 || numNums &lt; 2 || numSpecials &lt; 2) {
        alert("La password deve contenere almeno due maiuscole, due minuscole, due numeri e due caratteri speciali!");
        return false;
    }

    return true;
}

$(function () {
    var key = localStorage.getItem('pwd');

    if (key === null) {
        // Make a subclass of ProcessDialog
        function MyDialog( config ) {
            MyDialog.super.call( this, config );
        }
        OO.inheritClass( MyDialog, OO.ui.ProcessDialog );

        // Specify a name for .addWindows()
        MyDialog.static.name = 'myDialog';
        // Specify the static configurations: title and action set
        MyDialog.static.title = 'Password';
        MyDialog.static.actions = [
            { 
                flags: 'primary', 
                label: 'Bene', 
                action: 'save' 
            },
            { 
                flags: 'safe', 
                label: 'Annulla' 
            }
        ];

        // Customize the initialize() function to add content and layouts: 
        MyDialog.prototype.initialize = function () {
            MyDialog.super.prototype.initialize.call( this );
            this.panel = new OO.ui.PanelLayout( { 
                padded: true, 
                expanded: false 
            } );
            this.content = new OO.ui.FieldsetLayout();

            this.keyInput = new OO.ui.TextInputWidget();

            this.field = new OO.ui.FieldLayout( this.keyInput, { 
                label: 'Inserire la password per criptare/decriptare i dati sensibili', 
                align: 'top' 
            } );

            this.content.addItems( [ this.field ] );
            this.panel.$element.append( this.content.$element );
            this.$body.append( this.panel.$element );

            this.keyInput.connect( this, { 'change': 'onkeyInputChange' } );
        };

        // Specify any additional functionality required by the window (disable saving an invalid password, in this case)
        MyDialog.prototype.onkeyInputChange = function ( value ) {
            this.actions.setAbilities( {
                save: complexKey(key)
            } );
        };

        // Specify the dialog height (or don't to use the automatically generated height).
        MyDialog.prototype.getBodyHeight = function () {
            // Note that "expanded: false" must be set in the panel's configuration for this to work.
            // When working with a stack layout, you can use:
            //   return this.panels.getCurrentItem().$element.outerHeight( true );
            return this.panel.$element.outerHeight( true );
        };

        // Use getSetupProcess() to set up the window with data passed to it at the time 
        // of opening
        MyDialog.prototype.getSetupProcess = function ( data ) {
            data = data || {};
            return MyDialog.super.prototype.getSetupProcess.call( this, data )
            .next( function () {
                // Set up contents based on data
                // data.key
            }, this );
        };

        // Specify processes to handle the actions.
        MyDialog.prototype.getActionProcess = function ( action ) {
            if ( action === 'save' ) {
                // Create a new process to handle the action
                return new OO.ui.Process( function () {
                    var key = this.keyInput.getValue();
                    if (complexKey(key)) {
                      localStorage.setItem('pwd', key);
                      windowManager.closeWindow( myDialog, {} );
                    }
                }, this );
            }
            // Fallback to parent handler
            return MyDialog.super.prototype.getActionProcess.call( this, action );
        };

        // Use the getTeardownProcess() method to perform actions whenever the dialog is closed. 
        // This method provides access to data passed into the window's close() method 
        // or the window manager's closeWindow() method.
        MyDialog.prototype.getTeardownProcess = function ( data ) {
            return MyDialog.super.prototype.getTeardownProcess.call( this, data )
            .first( function () {
            // Perform any cleanup as needed
            }, this );
        };

        // Create and append a window manager.
        var windowManager = new OO.ui.WindowManager();
        $( document.body ).append( windowManager.$element );

        // Create a new process dialog window.
        var myDialog = new MyDialog({ size: 'medium' });

        // Add the window to window manager using the addWindows() method.
        windowManager.addWindows( [ myDialog ] );

        windowManager.openWindow( myDialog, {} );
    }

    $.getScript("http://localhost/wiki/images/images_wiki/resources/assets/aes.js", function(){
        var data = document.getElementsByClassName('encrypted');
        var elems = Array.from(data);
        debugger;
        var entries = elems.filter(function (e) {
            return e.innerText.startsWith('U2');
        });

        entries.map(function (e) {
            alert('DT = ' + e.innerText);
            var decrypted = CryptoJS.AES.decrypt(e.innerText, key);
            var name = decrypted.toString(CryptoJS.enc.Utf8);
            e.innerText = name;
        });
    });

    $( ".identifier" ).focusout(function() {
      var encrypted = CryptoJS.AES.encrypt($(this).val(), key);
      $(this).val(encrypted.toString());
    });
});


